mapper.select:
select *
from submit_resume
where user_phone = #{userPhone}
  and position_id = #{positionId}

select *
from submit_resume


        username like concat('%',#{username},'%')


        and position_name like concat('%',#{positionName},'%')


        and company_name = #{companyName}


        and category = #{category}


        and type = #{type}
